@js:
if(baseUrl.match(/pic/)){
result = java.getElements("class.book-player&&tag.script.9")}else{
var str = java.getElements("tag.script.3");
result = String(str).match(/url:'([^']+)'/)[1];
result = "<video src=\"" + result + "\" width=\"100%\" height=\"285px\" controls=\"controls\" ></video>";
result = "<html><head><meta charset=\"utf-8\"><meta name=\"viewport\" content=\"width=device-width,initial-scale=1,minimum-scale=1,maximum-scale=1,user-scalable=no\" /><style>html,body{margin:0;padding:0;width:100%;}</style></head><body>" + result + "</body></html>"
}

@js:
//获取总页数
var a = java.getElements(".pagelist@p@b@a");
var num = a.length;
//获取第一页内容
var content = java.getElements("#picg@img");
var doc = org.jsoup.Jsoup.parse(content);

//解析剩下页数,并追加剩余页数内容
var other = [];
var b="{{@@class.pagelist@a.-1@text}}"
if(b=="下一页")
{
for(var i = 2;i <= num;i++)
{
var url = baseUrl.replace(".html","")
url = url +"_" +i + ".html"
var doc = org.jsoup.Jsoup.connect(url).get();
other[i] =  doc.select("#picg").select("img");
}
}
var oall =other.join(" ")
var href = '<a href='+baseUrl+'>原文出处</a>'
 result = String(content)+String(oall)+String(href)

class.content-padded@img@src<js>
var pics=result.split("\n");
var html="";
for(i=0;i<pics.length;i++){
html+="<img src=\""+pics[i]+"\"/>\n";
}
result="<html><head><meta charset=\"utf-8\"><meta name=\"viewport\" content=\"width=device-width,initial-scale=1,minimum-scale=1,maximum-scale=1,user-scalable=no\" /></head>"+ "<body>"+ html+"</body></html>"
</js>

