window.onload=getTime;
function getTime(){
var today=new Date();
var year=today.getFullYear();//获取年
var month=today.getMonth()+1;//获取月
var date=today.getDate();//获取日
var day=today.getDay();//获取星期
var hours=today.getHours();//获取小时
var minutes=today.getMinutes();//获取分钟
var seconds=today.getSeconds();//获取秒
var milliseconds=today.getMilliseconds();//获取毫秒

//当???小于/等于/大于一个数值时，???显示为
if(hours<1){
hours="午夜 "+hours;
} else if(hours<7){
hours="凌晨 "+hours;
} else if(hours<12){
hours="上午 "+hours;
} else if(hours<13){
hours="正午 "+hours;
} else if(hours<19){
hours="下午 "+[hours-12];
} else if(hours<22){
hours="晚上 "+[hours-12];
} else if(hours<24){
hours="半夜 "+[hours-12];
}

if(day<1){
day="星期天";
} else if(day<2){
day="星期一";
} else if(day<3){
day="星期二";
} else if(day<4){
day="星期三";
} else if(day<5){
day="星期四";
} else if(day<6){
day="星期五";
} else if(day<7){
day="星期六";
}

if(minutes<10){
minutes="0"+minutes;
}
if(seconds<10){
seconds="0"+seconds;
}
var 显示=year+'年'+month+'月'+date+'日'+day+'<br>'+hours+":"+minutes+":"+seconds+"<sup><i>"+milliseconds;
$('时间').innerHTML=显示;
setInterval("getTime()",1000)
}
</script>
