@js:
var doc = org.jsoup.Jsoup.parse(result);
var title = doc.select("span#main h1").text();

//搜索和dt库解析
if(baseUrl.match(/av_Broadcast|18av_dt/)){
var path = String(doc).match(/php\?id=(\w{10,30})/)[1];
var obj = path.match(/_(\w+?)_(\w+)/);
var src = "https://fc.imgscloud.com/"+obj[1]+"/"+obj[2]+"/"+obj[2]+".m3u8"

//页面
html='<head><link href="//cdn.staticfile.org/dplayer/1.25.0/DPlayer.min.css" rel="stylesheet"><script src="//cdn.staticfile.org/dplayer/1.25.0/DPlayer.min.js"></script></head><div id="dplayer" style="width:100%;height:280px"></div><script>(function (){var dplayer = new DPlayer({element:document.getElementById("dplayer"),autoplay: true,lang: \'zh-cn\',video: {url: \"'+src+'\"}});})()</script><div>播放地址:<script type="text/javascript">function copyUrl2(){var Url2 = document.getElementById("fz");Url2.select();document.execCommand("Copy");alert("已复制好，粘贴到idm即可下载");}</script><input type="text" id="fz" value="'+src+'"><input type="button" onClick="copyUrl2()" value="点击复制代码" ></div>'

result = '<h5>'+title+'</h5>'+ html
}else {
//xp1库解析，此库点进去会出年龄确认弹窗，点下确认后退出，重新进入就能播放。
var src = "https://maa1804.com/"+String(doc).match(/xpmv.{5,18}html/)[0];

var video='<iframe src='+src+' width="100%" height="280px" frameborder="no" allowfullscreen="true">'
result = '<h5>'+title+'</h5>'+ video
}

