@js:
`<html>
<head>
<meta charset="utf-8">
<meta name="viewport" content="width=device-width,initial-scale=1.0,minimum-scale=1.0,maximum-scale=1.0,user-scalable=no"/>
<link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/viewerjs@1.11.2/dist/viewer.css" rel="external nofollow" rel="external nofollow">
<script src="https://cdn.jsdelivr.net/npm/viewerjs@1.11.2/dist/viewer.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/jquery@3.6.3/dist/jquery.min.js"></script>
</head>
<div class="page"></div>
<div class ="dz"  id="dz" width="100%"></div>
<div class="page"></div>
<script>
function i(url){
$.get(url,function(result){

html=result.match(/(<div class="(info_imgs|info_text)">[\\s\\S]*?)<div align='center'/)[1];

 page=result.match(/<div align='center' class='paging'>[\\s\\S]*?div>/);

 page=String(page).replace(/<a/g,"<button").replace(/\a>/g,"button>").replace(/<li.*?>|<\\/li>|<button>共\\d+页: <\\/button>|<div.*?>|<\\/div>/g,"");

 alert(html)
 //alert(page)

 window.scroll(0,0)
 $("#dz").html(html);
 $(".page").html(page);
});
}
$(function(){
url=$(this).val();
i(url);
});
$(function(){
$(document).on("click","button",function(){
	url="{{source.sourceUrl}}"+"plus/"+$(this).attr("href");
	//alert(url)
	i(url);
	})});
</script>
</html>`

